import React from 'react';
import DayPicker from 'react-day-picker';

const birthdays = {
  3: ['Mirko', 'Gianni'],
  8: ['Elena'],
  9: ['Irene'],
  12: ['Paolo', 'Giorgia'],
  18: ['Claudia'],
  22: ['Maria', 'Luigi'],
  25: ['Simone'],
  26: ['Marta'],
};

function renderDay(day) {
  const date = day.getDate();
  const dateStyle = {
    position: 'absolute',
    color: 'lightgray',
    bottom: 0,
    right: 0,
    fontSize: 20,
    fontWeight: 500,
  };
  const birthdayStyle = { fontSize: '0.8em', textAlign: 'left' };
  const cellStyle = {
    height: '100px',
    width: '100px',
    border: '1px solid #BDBDBD',
    position: 'relative',
  };
  return (
    <div style={cellStyle}>
      <div style={dateStyle}>{date}</div>
      {birthdays[date]
        && birthdays[date].map((name, i) => (
          <div key={i} style={birthdayStyle}>
            üéÅ
            {' '}
            {name}
          </div>
        ))}
    </div>
  );
}

export default function Example() {
  return (
    <div>
      <DayPicker
        canChangeMonth={false}
        className="Birthdays"
        renderDay={renderDay}
      />
      <style jsx>
        {`
          .DayPicker-Day{
            margin:0;
            padding:0 !important;
          }
        `}
      </style>
    </div>
  );
};
