import React, { Fragment } from 'react';
import styled from 'styled-components';

const calendar = () => {
  console.log('calendar');
  const today = new Date();
  const date = new Date();

  console.log(today);
  const Button = styled.button`
    background-color:black;
    color:white;
    width:100px;
  `;
  const Table = styled.table`
    tbody .day_td {
      width:100px;
      border:1px solid black;
    }
  `;
  return (
    <Fragment>
      <h1>calendar</h1>
      <Table align="center" id="calendar_table">
        <thead>
          <tr>
            <td><Button>prev</Button></td>
            <td colSpan="5">현재날짜</td>
            <td><Button>next</Button></td>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td className="day_td">일s</td>
            <td className="day_td">월</td>
            <td className="day_td">화</td>
            <td className="day_td">수</td>
            <td className="day_td">목</td>
            <td className="day_td">금</td>
            <td className="day_td">토</td>
          </tr>
        </tbody>
      </Table>
    </Fragment>
  );
};

export default calendar;
